import React, {useEffect, useContext, useState, Fragment} from "react"
import useFetch from 'hooks/useFetch'
import {Link, Redirect} from "react-router-dom"
import  Loading from 'components/loading'
import  TagList from 'components/tagList'
import  ErrorMessage from 'components/errorMessage'
import {CurrentUserContext} from 'contexts/currentUser'
import FollowUser from "components/followUser"
import AddToFavorites from 'components/addToFavorite'
import CommentForm from 'components/commentForm'
import CommentsFeed from 'components/commentsFeed'

const Article = props => {

    const [initialValues, setInitialValues] = useState(null)

    const slug = props.match.params.slug
    const apiUrl = `/articles/${slug}`
    const [
        {
        response: fetchArticleResponse,
        error: fetchArticleError,
        isLoading: fetchArticleIsLoading
        },
        doFetch
    ] = useFetch(apiUrl)

    const [{response: deleteArticleResponse}, doDeleteArticle] = useFetch(apiUrl)
    const [currentUserState] = useContext(CurrentUserContext)

    const [isSuccessfullDelete, setIsSuccessfullDelete] = useState(false)

    //post comments
    const apiUrlComment =  `/articles/${slug}/comments`
    const [
        {
            response: responseCommentsPost,
        },
        doFetchCommentPost
    ] = useFetch(apiUrlComment)

    const [
        {
            response: responseCommentsGet,
        },
        doFetchCommentGet
    ] = useFetch(apiUrlComment)

    const [commentsLoading, setCommentsLoading] = useState(false)

    const isAuthor = () => {
        if (!fetchArticleResponse || !currentUserState.isLoggedIn) {
            return false
        }
        return (
            fetchArticleResponse.article.author.username === currentUserState.currentUser.username
        )
    }

    const deleteArticle = () => {
        doDeleteArticle({
            method: 'delete'
        })

        //delete all comments?
    }

    //submit new post
    const handleSubmit = comment => {
        doFetchCommentPost({
            method: 'post',
            data: {
                comment
            }
        })

        setInitialValues({
            body: ''
        })

        setCommentsLoading(true)
    }


    useEffect(() => {
        setCommentsLoading(false)
        doFetch()
        doFetchCommentGet()
    },[doFetch, doFetchCommentGet, commentsLoading])

    useEffect(() => {
        if (!deleteArticleResponse) {
            return
        }
        setIsSuccessfullDelete(true)
    },[deleteArticleResponse])

    if (isSuccessfullDelete) {
        return <Redirect to="/"/>
    }



    return (
        <div className="article-page">
            <div className="banner">
                {!fetchArticleIsLoading && fetchArticleResponse && (
                    <div className="container">
                        <h1>{fetchArticleResponse.article.title}</h1>
                        <div className="article-meta">
                            <Link to={`/profiles/${fetchArticleResponse.article.author.username}`}>
                                <img src={fetchArticleResponse.article.author.image} alt="" />
                            </Link>
                            <div className="info">
                                <Link to={`/profiles/${fetchArticleResponse.article.author.username}`}>
                                    {fetchArticleResponse.article.author.username}
                                </Link>
                                <span className="date">{fetchArticleResponse.article.createdAt}</span>
                            </div>
                            {isAuthor() && (
                                <span>
                                    <Link
                                        className="btn btn-outline-secondary btn-sm"
                                        to={`/articles/${fetchArticleResponse.article.slug}/edit`}
                                    >
                                    <i className="ion-edit"></i>
                                    Edit Article
                                    </Link>
                                    <button
                                        className="btn btn-outline-danger btn-sm"
                                        onClick={deleteArticle}
                                    >
                                        <i className="ion-trash-a"></i>
                                        Delete article
                                    </button>
                                </span>
                            )}

                            {currentUserState.isLoggedIn
                            && currentUserState.currentUser.username !== fetchArticleResponse.article.author.username
                            && (
                                <Fragment>
                                    <div className="pull-xs-right">
                                        <FollowUser
                                            isFollowed={fetchArticleResponse.article.author.following}
                                            userSlug={fetchArticleResponse.article.author.username}
                                        />
                                    </div>

                                    <div className="pull-xs-right">
                                    <AddToFavorites
                                    isFavorited={fetchArticleResponse.article.favorited}
                                    favoritesCount={fetchArticleResponse.article.favoritesCount}
                                    articleSlug={fetchArticleResponse.article.slug}
                                    />
                                    </div>
                                </Fragment>
                            )}
                        </div>
                    </div>
                )}
            </div>
            <div className="container page">
                {fetchArticleIsLoading && <Loading/>}
                {fetchArticleError && <ErrorMessage/>}
                {!fetchArticleIsLoading && fetchArticleResponse && (
                    <div className="row article-content">
                        <div className="col-xs-12">
                            <div>
                                <p>{fetchArticleResponse.article.body}</p>
                            </div>
                            <TagList tags={fetchArticleResponse.article.tagList} />
                        </div>
                    </div>
                )}
            </div>
            <div>
                <hr/>
                <CommentForm
                    errors={(responseCommentsPost && responseCommentsPost.errors) || {} }
                    initialValues={initialValues}
                    onSubmit={handleSubmit}
                />
            </div>


            <div className="container page">
                <div className="row">
                    <div className="col-md-9">
                        {responseCommentsGet && (
                            <CommentsFeed
                                comments={responseCommentsGet.comments}
                                articleSlug = {slug}
                            />
                        )}
                    </div>

                </div>
            </div>


        </div>



    )
}

export default Article